mapper.select:
select * from advertiser

	1=1
	
		and user_name like CONCAT(CONCAT('%',#{userName}),'%')
	
	
		and real_name like CONCAT(CONCAT('%',#{realName}),'%')
	
	
		and country=#{country}
	
	
		and area=#{area}
	
	
		and city=#{city}
	

order by create_time desc

mapper.update:
update advertiser
user_name=#{userName}, password=#{password}, real_name=#{realName}, mobile=#{mobile}, email=#{email}, country=#{country}, area=#{area}, city=#{city}, birthday=#{birthday}, remark=#{remark}, status=#{status}
where id = #{id}
