mapper.select:
select * from business 

	1=1
	
		and user_name like CONCAT(CONCAT('%',#{userName}),'%')
	
	
		and real_name like CONCAT(CONCAT('%',#{realName}),'%')
	
	
		and country_code = #{countryCode}
	
	
		and area_code = #{areaCode}
	
	
		and city_code = #{cityCode}
	
	order by create_time desc

mapper.update:
update business
user_name=#{userName}, real_name=#{realName}, country_code=#{countryCode}, area_code=#{areaCode}, city_code=#{cityCode}, mobile=#{mobile}, email=#{email}, facebook=#{facebook}, weixin=#{weixin}, remark=#{remark}, birthday=#{birthday}, status=#{status}
where id = #{id}
