mapper.select:
select p.*, b.user_name from place p left join business b on p.business_id=b.id

	1=1
	
		and p.cname like CONCAT(CONCAT('%',#{cname}),'%')
	
	
		and p.ename like CONCAT(CONCAT('%',#{ename}),'%')
	
	
		and p.type = #{type}
	
	
		and p.country_code = #{countryCode}
	
	
		and p.area_code = #{areaCode}
	
	
		and p.city_code = #{cityCode}
	
	order by p.create_time desc

mapper.update:
update place
cname=#{cname}, ename=#{ename}, type=#{type}, scale=#{scale}, average_daily_flow=#{averageDailyFlow}, country_code=#{countryCode}, area_code=#{areaCode}, city_code=#{cityCode}, address=#{address}, zip_code=#{zipCode}, business_id=#{businessId}
where id = #{id}

mapper.select:
SELECT p.id, p.cname, p.type, p.country_code, p.area_code, p.city_code FROM place p WHERE NOT EXISTS ( SELECT * FROM orders_detail od LEFT JOIN orders o ON od.orders_id = o.id WHERE p.city_code = od.city_code
AND ((o.start_time >#{startTime} AND o.start_time < #{endTime}) OR (o.end_time >#{startTime} AND o.end_time <#{endTime}))
)
and p.type = #{type}
AND p.city_code in(${cityCode})
