mapper.select:
select * from place 

	1=1
	
		and cname like CONCAT(CONCAT('%',#{cname}),'%')
	
	
		and ename like CONCAT(CONCAT('%',#{ename}),'%')
	
	
		and type = #{type}
	
	
		and country_code = #{countryCode}
	
	
		and area_code = #{areaCode}
	
	
		and city_code = #{cityCode}
	
	order by create_time desc

mapper.update:
update place
cname=#{cname}, ename=#{ename}, type=#{type}, scale=#{scale}, average_daily_flow=#{averageDailyFlow}, country_code=#{countryCode}, area_code=#{areaCode}, city_code=#{cityCode}, address=#{address}, zip_code=#{zipCode}, business_id=#{businessId}
where id = #{id}
