mapper.select:
        SELECT am.title,a.real_name,od.country_code,od.area_code,od.city_code,o.start_time,
        o.end_time,o.status FROM orders o LEFT JOIN orders_detail od ON o.id=od.orders_id 
        LEFT JOIN advertisement am ON am.id=o.advertisement_id LEFT JOIN advertiser a 
        ON a.id=am.advertiser_id where o.status in (2,4)


			AND od.country_code = #{countryCode}


			AND od.area_code = #{areaCode}


			AND od.city_code = #{cityCode}


			AND ((o.start_time >=#{startTime} AND o.start_time <=#{endTime}) OR(o.end_time >=#{startTime} AND o.end_time <=#{endTime})
			  OR (o.start_time >=#{startTime} AND o.end_time <=#{endTime}) OR (o.start_time <=#{startTime} AND o.end_time >=#{endTime})
			)


			AND am.title like CONCAT('%',#{title},'%' )

mapper.update:
update orders
advertiser_id=#{advertiserId}, advertisement_id=#{advertisementId}, auditor=#{auditor}, audit_time=#{auditTime}, status=#{status}
where id = #{id}

update orders
ahead_modify_time=#{aheadModifyTime}, auditor=#{auditor}, audit_time=#{auditTime}, end_time=#{endTime}, remark=#{remark}, original_end_time=#{originalEndTime}
where id = #{id}
